import vk_api #API vk.comimport os     #команды для консолиimport wget   #скчивание файлаfrom time import sleep #таймерtoken = open('token.txt')token = token.readline()token = token[:-1]vk_session = vk_api.VkApi(token=token)vk = vk_session.get_api() #авторизацияgg = vk.docs.get(owner_id= -212708924)os.remove('text.txt') #удаление старого файлаprint()wget.download(gg['items'][0]['url'], 'text.txt') #скачивание файлаprint("\n\033[35m файл успешно скачен")sleep(1)